:root{
    --color-primary: #2584ff;
    --color-secondary: #00d9ff;
    --color-accent: #ff3400;
    --color-headings: #1b0760;
    --color-body: #918ca4;
    --color-body-darker: #5c5577;
    --color-border: #ccc;
    --border-radious: 30px
}

*, *::after, *::before{
    box-sizing: border-box;
}
/* TYPOGRAPHY */

::selection{
    background: var(--color-primary);
    color: #fff;
}

html{ /*62.5% of 16px(default value) this means 10px so, 1rem =10px */
    font-size: 62.5%;
}
body{
    font-family: inter, arial, Arial, Helvetica, sans-serif;
    font-size: 2.4rem;
    line-height: 1.5;
    color: var(--color-body);
}

h1,h2,h3{
    color: var(--color-headings);
    margin-bottom: 1rem;
    line-height: 1.1;
}

h1{
    font-size: 7rem;
}

h2{
    font-size: 4rem;
}

h3{
    font-size: 3rem;
    font-weight: 500;
}
p{
    margin-top: 0;
}

@media screen and (min-width: 1024px){
    body{
        font-size: 1.8rem;
    }
    h1{
        font-size: 8rem;
    }
    h2{
        font-size: 4rem;
    }
    h3{
        font-size: 2.4rem;
    }
}

/* LINKS */

a{
    text-decoration: none;
}

.link-arrow{
    color: var(--color-accent);
    text-transform: uppercase;
    font-size: 2rem;
    font-weight: bold;
}

.link-arrow::after{
    content: '-->';
    margin-left: 5px;
    transition: margin 0.15s;
}

.link-arrow:hover::after{
    margin-left: 10px;
}

@media screen and (min-width: 1024px){
    .link-arrow{
        font-size: 1.5rem;
    }
}

/* BADGES */

.badge{
    border-radius: 20px;
    font-size: 2rem;
    font-weight: 600;
    padding: 0.5rem 2rem;
    white-space: nowrap;
}

.badge--primary{
    background: var(--color-primary);
    color: #fff;
}

.badge--secondary{
    background: var(--color-secondary);
    color: #fff;
}

.badge--small{
    font-size: 1.6rem;
    padding: 0.5rem 1.5rem;
}

@media screen and (min-width: 1024px){
    .badge{
        font-size: 1.5rem;
    }
    .badge--small{
        font-size: 1.2rem;
    }
}

/* LIST */

.list{ /*structure*/
    list-style: none;
    padding-left: 0;
}

.list--inline .list__item{ /*SKIN*/
    display: inline-block;
    margin-right: 2rem;
}

.list--tick{
    list-style-image: url(../Images/tick.svg);
    padding-left: 3rem;
    color: var(--color-headings);
}


.list--tick .list__item{
    padding-left: 0.5rem;
    margin-bottom: 1rem;
}

@media screen and (min-width: 1024px){
    .list--tick .list__item{
        padding-left: 0rem;
    }
}

/* ICONS */

.icon{ /*Structure*/
    width: 40px;
    height: 40px;
}

.icon--small{
    width: 30px;
    height: 30px;
}

.icon--primary{
    fill: var(--color-primary); /*THIS PROPERTY ONLY APPLY FOR SVG IMAGES*/
}

.icon--white{
    fill: #fff;
}

.icon-container{
    background: #f3f9fa;
    width: 64px; /*We use PX because the mobile or desktop icon are allways the same size*/
    height: 64px;
    border-radius:100%;
    display: inline-flex; /*we don not use "inline" because we do not want the line break*/
    justify-content: center;
    align-items: center;
}

.icon-container--accent{
    background: var(--color-accent);
}

/* Buttons */

.btn{
    border-radius: 40px;
    border: 0;
    cursor: pointer; /* the button looks like an anchor*/
    font-size: 1.8rem;
    font-weight: 600;
    margin: 1rem 0;
    outline: 0;
    padding: 2rem 4vw;
    text-align: center;
    text-transform: uppercase;
    white-space: nowrap; /*i do not want the text in the button wrap onto a second line*/
    transition: background 0.7s, color 0.7s;
}

.btn .icon{
    width: 2rem;
    height: 2rem;
    margin-right: 1rem;
    vertical-align: middle;
}

.btn--primary{
    background: var(--color-primary);
    color: #f3f9fa;    
}

.btn--primary:hover{
    background: #085cc9;
    color: #f3f9fa;
}

.btn--secondary{
    background: var(--color-secondary);
    color: #f3f9fa;
    border: var(--color-secondary);
}

.btn--secondary:hover{
    background: #0c414a;
    color: #f3f9fa;
}

.btn--accent{
    background: var(--color-accent);
    color: #f3f9fa;
}

.btn--accent:hover{
    background: #651a07;
    color: #f3f9fa;
}

.btn--stretched{
    padding-left: 6rem;
    padding-right: 6rem;
}

.btn--block{
    width: 100%;
    display: inline-block;
}

.btn--outline{
    background: #f3f9fa;
    color: var(--color-headings);
    border: 2px solid var(--color-headings);
}

.btn--outline:hover{
    background: var(--color-headings);
    color: #f3f9fa;
}

@media screen and (min-width: 1024px){
    .btn{
        font-size: 3rem;
    }
}

/* INPUTS */
.input{
    border-radius: var(--border-radious);
    border: 1px solid var(--color-border);
    color: var(--color-headings);
    font-size: 2rem;
    outline: 0;
    padding: 1.5rem 3.5rem;
}

::placeholder{
    color: #cdcbd7;
}

.input-group{
    border: 1px solid var(--color-border);
    border-radius: var(--border-radious);
    display: flex;
}

.input-group .input{
    border: 0;
    flex-grow: 1;
    padding: 1.5rem 2rem;
    width: 0;
}

.input-group .btn{
    margin: 4px;
}
@media screen and (min-width: 1024px){
    .input{
        font-size: 1.5rem;
    }
}

/* CARD */

.card{
    border-radius: 7px;
    box-shadow: 0 0 20px 10px #f3f3f3;
    overflow: hidden;
}

.card__header, .card__body{
    padding: 2rem 3rem;
}

.card--primary .card__header{
    background: var(--color-primary);
    color: #fff;
}

.card--secondary .card__header{
    background: var(--color-secondary);
    color: #fff;
}

.card--primary .badge--primary{
    background: #126de4;
}

.card--secondary .badge--secondary{
    background: #02cdf1;
}

/* PLAN */

.plan{
    transition: transform 0.2s ease-out;
    margin: 0 1rem;
}


.plan__name{
    color: #fff;
    margin: 0;
    font-weight: 500;
    font-size: 2.4rem;
}

.plan__price{
    font-size: 6rem;
}

.plan__billing-cycle{
    font-size: 2,4rem;
    font-weight: 300;
    opacity: 0.8;
    margin-right: 1rem;
}

.plan__description{
    font-size: 2rem;
    font-weight: 300;
    letter-spacing: 1px;
    display: block;
}

.plan .list__item{
    margin-bottom: 2rem;
}

.plan--popular{
    transform: scale(1.1);
}

.plan--popular .card__header{
    position: relative;
}

.plan--popular .card__header::before{
    content: url(../Images/popular.svg);
    width: 40px;
    display: inline-block;
    position: absolute;
    top: -6px;
    right: 5%;
}

.plan:hover{
    transform: scale(1.05);
}

.plan--popular:hover{
    transform: scale(1.15);
}

@media screen and (min-width: 1024px) {
    .plan__name{
        font-size: 1.4rem;
    }
    .plan__price{
        font-size: 5rem;
    }
    .plan__billing-cycle{
        font-size: 1.6rem;
    }
    .plan__description{
        font-size: 1.7rem;
    }
}

/* Media */

.media{
    display: flex;
    margin-bottom: 4rem;
    margin-left: 1rem;
    margin-right: 1rem;
}

.media__image{
    margin-top: 1rem;
}

.media__title{
    margin-top: 0;
}

.media__body{
    margin: 0 2rem;
}

.media__text{
    margin-bottom: 10rem;
}

/* Quotes */

.quote{
    font-size: 3rem;
    font-style: italic;
    color: var(--color-body-darker);
    line-height: 1.3;
}

.quote__text::before{
    content: open-quote;
}

.quote__text::after{
    content: close-quote;
}

.quote__author{
    font-size: 3rem;
    font-weight: 500;
    font-style: normal;
    margin-bottom: 0;
}

.quote__organization{
    color: var(--color-headings);
    opacity: 0.4;
    font-size: 2rem;
    font-style: normal;
}

.quote__line{
    position: relative;
    bottom: 15px;
}

@media screen and (min-width: 1024px){
    .quote{
        font-size: 2rem;
    }
    .quote__author{
        font-size: 2.4rem;
    }
    .quote.organization{
        font-size: 1.6rem;
    }
}

/* Grid */

.grid{
    display: grid;
}

@media screen and (min-width: 768px){
    .grid--1x2{
        grid-template-columns: repeat(2, 1fr); /* fr means all the space available*/
    }
}

@media screen and (min-width: 1024px){
    .grid--1x3{
        grid-template-columns: repeat(3, 1fr);
    }
}

/* Testimonial */

.testimonial{
    padding: 4rem;
}

.testimonial__image{
    width: 100%;
}

.testimonial__image-container{
    position: relative;
}

.testimonial__icon{
    position: absolute;
    top: 3rem;
    right: -32px;
}

.testimonial .icon-container{
    overflow: hidden;
}

@media screen and (min-width: 319px) and (max-width: 374px){
    .testimonial__image-container{
        right: 17px;
    }
    .testimonial__icon{
        right: -17px;
    }
    .icon-container{
        width: 36px;
        height: 36px;
    }
    .icon--small{
        width: 25px;
        height: 25px;
    }
}
@media screen and (min-width: 767px){
    .testimonial .quote, .testimonial .quote__author{
        font-size: 2.4rem;
    }
    .testimonial .quote{
        margin-left: 6rem;
    }
}

/* Callouts */

.callout{
    padding: 4rem;
    border-radius: 5px;
}

.callout--primary{
    background:  var(--color-primary);
    color: #fff;
}

.callout__heading{
    margin-top: 0;
    color: #fff;
    font-size: 3rem;
}

.callout .btn{
    justify-self: center; /*in stead of use "justify-items"  we use justify-self for the grid item*/
    align-self: center;
}

.callout__content{
    text-align: center;
}

@media screen and (min-width: 768px){
    .callout .grid--1x2{
        grid-template-columns: 1fr auto; /*first "1fr" means use allthe space available in that columns
         the "auto" means use the space necesary for the content. So i got 2 columns differents sizes*/
    }
    
    .callout__content{
        text-align: left;
    }
    .callout .btn{
        justify-self: start;
        margin: 0 2rem;
    }
}

/* Collapsible */

.collapsible__header{
    display: flex;
    justify-content: space-between;
}

.collapsible__title{
    margin-top: 0;
    font-size: 3rem;
}

.collapsible__icon{
    transform: rotate(-90deg);
    transition: transform .3s;    
}

.collapsible__content{
    max-height:0;
    overflow: hidden;
    opacity:0;
    transition: max-height 0.3s, opacity 0.4s;
}

.collapsible--expanded .collapsible__icon{
    transform: rotate(0);
}

.collapsible--expanded .collapsible__content{
    max-height: 100vh;
    opacity: 1;
}

/* Blocks */

.block{
    --padding-vertical: 6rem;
    padding: var(--padding-vertical)  0.5rem;
}

.block__title{
    margin-top: 0;
}

.block--dark{
    background: #000;
    color: #7b858b;
}

.block__header{
    text-align: center;
    margin-bottom: 4rem;
}

.block--dark h1,
.block--dark h2,
.block--dark h3{
    color: #fff;
}

.block--skewed-right{  /*Skewed means diagonal in the down background line*/
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 90%);
}

.block--skewed-left{  /*Skewed means diagonal in the down background line*/
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);
}

.container{
    max-width:1140px;
    margin: 0 auto;
}

.block .block__text{
    padding: 0  2rem;
}

/* Navigation Bar */

.nav{
    background: #000;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    padding: 0 1rem;
    align-items: center;
}

.nav__list{
    width: 100%;
    margin: 0;
}

.nav__item{
    padding: 0.5rem 2rem;
    border-bottom: 2px solid #222;
}

.nav__item>a{
    color: #d2d0db;
    transition: color .3s;
}

.nav__item>a:hover{
    color: #fff;
}

.nav__toggler{
    opacity: 0.5;
    transition: box-shadow 0.15s;
    cursor: pointer;
}

.nav.collapsible--expanded .nav__toggler{
    opacity: 1;
    box-shadow:  0 0 0 3px #666;
    border-radius: 5px;
}

.nav__brand{
    transform: translateY(5px);
}

@media screen and (min-width: 768px){
    .nav__list{
        width: auto;
        display: flex;
        font-size: 1.6rem;
        max-height: 100%;
        opacity: 1;
    }
    .nav__toggler{
        display: none;
    }
    .nav__item{
        border-bottom: 0;
    }
}

/* Hero */

.hero__tagline{
    font-size: 2rem;
    color: #b9c3cf;
    letter-spacing: 1px;
    margin: 2rem 0 5rem;
}

.hero__image{
    width: 100%;
}

@media screen and (min-width: 768px){
    .hero{
        padding-top: 0;
    }

    .hero__content{
        text-align: left;
        align-self: center;
    }
}

/* Domain Block */

.block-domain .input-group{
    box-shadow: 0 0 30px 20px #e6ebee;
    border: 0;
    margin: 4rem 1rem;
}

.block-domain__prices{
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(2, 6rem);
    font-size: 2rem;
    font-weight: 600;
    justify-items: center;
    max-width: 700px;
    margin: 3rem auto;
    max-width: 700px;
    color: var(--color-headings);
}

.block-domain__badge:hover{
    border-radius: 20px;
    font-size: 2rem;
    font-weight: 600;
    padding: 0.5rem 2rem;
    white-space: nowrap;
    background: var(--color-secondary);
    color: #fff;
}

@media screen and (min-width: 768px){
    .block-domain__prices{
        grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
    }
}

/* Plans Block */

.block-plans .grid{
    gap: 8rem 4rem;
}

.block-plans .card{
    max-width: 500px;
    margin: 0 auto;
}

/* Features */

.feature{
    gap: 4rem 2rem;
    margin: 12rem 4rem;
}

.feature:first-of-type{
    margin-top: 6rem;
}

.feature__heading{
    margin: 1rem 0;
}

.feature__image{
    width: 100%;
}

@media screen and (min-width: 768px) { /*It is not working*/
    .feature:nth-of-type(even) .feature__content {
        order: 2;
    }
}

/* Showcase Block */

.block-showcase__image > img{
    width: 100%;
}

@media screen and (min-width: 768px){
.block-showcase .grid{
    grid-template-columns: 50% 50%;
}

    .block-showcase__image{
        justify-self: end;
    }
    .block-showcase__image > img{
        width: auto;
        max-width: 700px;
    }
}

/* Footer */

.footer{
    background: #232323;
}

.footer a{
    color: #777;
    transition: color .3s;
}

.footer a:hover{
    color: #fff;
}

.footer__section{
    padding: 2rem;
    border-bottom: 1px solid #393939;
}

.footer__Section .list{
    margin-top: 0;
}

.footer__title{
    text-transform: uppercase;
    font-weight: 600;
}

.footer__brand{
    margin-top: 5rem;
    text-align: center;
}

.footer__brand img{
    /* TODO: consider refactoring this and applying it to all images. */
    width: 100%;
    max-width: 230px;
}

.footer__copyright{
    font-size: 2.1rem;
    color: #fff;
    opacity: .3;
}

@media screen and (min-width: 768px){
    .footer__sections{
        grid-template-columns: repeat(auto-fit,  minmax(10rem, 1fr));
    }
    .footer .collapsible__icon{
        display: none;
    }
    .footer .collapsible__content{
        opacity: 1;
        max-height: 100%;
    }
    .footer__brand{
        order: -1;
        margin-top: 1rem;
    }
    .footer__copyright{
        font-size: 1.5rem;
    }
    .footer__section{
        border: 0;
    }
    .footer__title{
        font-size: 1.6rem;
    }
}
